import 'dart:convert';

import 'package:flutter/material.dart';
import 'package:intl/intl.dart';
import 'package:http/http.dart' as http;

import '../../services/auth_service.dart';  // Import http package

class PaymentPage extends StatefulWidget {
  const PaymentPage({super.key});

  @override
  _PaymentPageState createState() => _PaymentPageState();
}

class _PaymentPageState extends State<PaymentPage> {
  DateTime? selectedDate;
  String? selectedTimeSlot;

  final List<String> timeSlots = [
    "06:00 AM - 07:00 AM",
    "07:00 AM - 08:00 AM",
    "08:00 AM - 09:00 AM",
    "05:00 PM - 06:00 PM",
    "06:00 PM - 07:00 PM",
    "07:00 PM - 08:00 PM",
  ];

  void _selectDate() async {
    DateTime? pickedDate = await showDatePicker(
      context: context,
      initialDate: DateTime.now(),
      firstDate: DateTime.now(),
      lastDate: DateTime.now().add(const Duration(days: 30)),
    );

    setState(() {
      selectedDate = pickedDate;
    });
    }

  // Function to call the API
  Future<void> makePayment(String paymentMethod) async {
    if (selectedDate == null || selectedTimeSlot == null) {
      _showErrorSnackbar();
      return;
    }

    final futsalId = ModalRoute.of(context)?.settings.arguments as String?;

    // Example API endpoint
    final apiUrl = 'http://127.0.0.1:4001/api/bookings';
    String? token = await AuthService().getToken();
    print(token);
    print(futsalId);
    final data = json.encode({
      'futsalId': futsalId ?? '',
      'paymentMethod': paymentMethod,
      'bookingDate': DateFormat('yyyy-MM-dd').format(selectedDate!),
      'timeSlot': selectedTimeSlot!,
      'status':'confirmed'

    });

    final response = await http.post(
      Uri.parse(apiUrl),
      headers: {
        'Content-Type': 'application/json',
        'Authorization': 'Bearer $token',
      },
      body: data,
    );
    print(response.statusCode);

    if (response.statusCode == 201) {
      print('Payment successful');
      Navigator.pop(context);
      ScaffoldMessenger.of(context).showSnackBar(
        const SnackBar(
          content: Text("Booking successful!"),
          backgroundColor: Colors.green,
        ),
      );
      // Handle successful payment response here (e.g., show a success message or navigate to another screen)
    } else {
      print('Payment failed');
      // Handle failure (e.g., show an error message)
    }
  }

  void _showErrorSnackbar() {
    ScaffoldMessenger.of(context).showSnackBar(
      const SnackBar(
        content: Text("Please select a booking date and time slot."),
        backgroundColor: Colors.red,
      ),
    );
  }

  @override
  Widget build(BuildContext context) {
    return Scaffold(
      appBar: AppBar(
        title: const Text('Book Your Futsal'),
        backgroundColor: Colors.deepPurple,
      ),
      body: Padding(
        padding: const EdgeInsets.all(16.0),
        child: Column(
          crossAxisAlignment: CrossAxisAlignment.start,
          children: [
            const Text(
              'Select Booking Date',
              style: TextStyle(fontSize: 18, fontWeight: FontWeight.bold),
            ),
            const SizedBox(height: 10),
            InkWell(
              onTap: _selectDate,
              child: Container(
                padding: const EdgeInsets.symmetric(vertical: 12, horizontal: 16),
                decoration: BoxDecoration(
                  color: Colors.grey[200],
                  borderRadius: BorderRadius.circular(10),
                  border: Border.all(color: Colors.grey),
                ),
                child: Row(
                  mainAxisAlignment: MainAxisAlignment.spaceBetween,
                  children: [
                    Text(
                      selectedDate == null
                          ? 'Choose a date'
                          : DateFormat('yyyy-MM-dd').format(selectedDate!),
                      style: const TextStyle(fontSize: 16),
                    ),
                    const Icon(Icons.calendar_today, color: Colors.deepPurple),
                  ],
                ),
              ),
            ),
            const SizedBox(height: 20),
            const Text(
              'Select Time Slot',
              style: TextStyle(fontSize: 18, fontWeight: FontWeight.bold),
            ),
            const SizedBox(height: 10),
            Container(
              padding: const EdgeInsets.symmetric(horizontal: 12),
              decoration: BoxDecoration(
                color: Colors.grey[200],
                borderRadius: BorderRadius.circular(10),
                border: Border.all(color: Colors.grey),
              ),
              child: DropdownButton<String>(
                isExpanded: true,
                underline: const SizedBox(),
                hint: const Text("Choose a time slot"),
                value: selectedTimeSlot,
                onChanged: (newValue) {
                  setState(() {
                    selectedTimeSlot = newValue;
                  });
                },
                items: timeSlots.map((slot) {
                  return DropdownMenuItem(
                    value: slot,
                    child: Text(slot, style: const TextStyle(fontSize: 16)),
                  );
                }).toList(),
              ),
            ),
            const SizedBox(height: 30),
            const Text(
              'Select Payment Method',
              style: TextStyle(fontSize: 18, fontWeight: FontWeight.bold),
            ),
            const SizedBox(height: 20),
            _buildPaymentButton(
              label: "Pay with eSewa",
              imageUrl: 'data:image/png;base64,',
              paymentMethod: 'eSewa',
            ),
            const SizedBox(height: 20),
            _buildPaymentButton(
              label: "Pay with Khalti",
              imageUrl: 'data:image/png;base64,',
              paymentMethod: 'Khalti',
            ),
          ],
        ),
      ),
    );
  }

  Widget _buildPaymentButton(
      {required String label, required String imageUrl, required String paymentMethod}) {
    return ElevatedButton(
      onPressed: () {
        makePayment(paymentMethod);
      },
      style: ElevatedButton.styleFrom(
        backgroundColor: Colors.deepPurple,
        foregroundColor: Colors.white,
        padding: const EdgeInsets.symmetric(vertical: 15, horizontal: 25),
        shape: RoundedRectangleBorder(borderRadius: BorderRadius.circular(10)),
      ),
      child: Row(
        mainAxisSize: MainAxisSize.min,
        children: [
          const SizedBox(width: 10),
          Text(
            label,
            style: const TextStyle(fontSize: 16),
          ),
        ],
      ),
    );
  }
}
